// 此处用于存放所有登录相关的接口请求
import request from '@/utils/request'

// 员工改变支付方式
export const employeePayment = (id, way) => {
  return request.post('/employee/payment', {
    id,
    way,
    address,
    bankname,
    banknumber
  })
}
