import Vue from 'vue'
import Vuex from 'vuex'
import { getInfo, setInfo, removeInfo } from '@/utils/storage'

Vue.use(Vuex)

export default new Vuex.Store({
  namespaced: true,
  state () {
    return {
      // 个人权证相关
      userInfo: getInfo()
    }
  },
  mutations: {
    // 所有mutations的第一个参数，都是state
    setUserInfo (state, obj) {
      state.userInfo = obj
      setInfo(obj)
    }
  },
  actions: {
    logout (context) {
      // 个人信息要重置
      context.commit('removeInfo')
    }
  },
  getters: {
    token (state) {
      return state.userInfo.token
    }
  }
})
