// 此处用于存放所有登录相关的接口请求
import request from '@/utils/request'

// . 登录接口
export const login = (mobile, smsCode) => {
  return request.post('/passport/login', {
    form: {
      isParty: false,
      partyData: {},
      mobile,
      smsCode
    }
  })
}
